import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Spinner, Alert } from 'react-bootstrap';
import { FaSearch } from 'react-icons/fa';

const Home = () => {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) {
      setError('Please enter a valid VIN.');
      return;
    }
    setError('');
    setLoading(true);

    try {
      // Fetch data from NHTSA API
      const response = await fetch(
        `https://vpic.nhtsa.dot.gov/api/vehicles/decodevinvalues/${searchTerm}?format=json`
      );
      const result = await response.json();

      console.log('NHTSA API Response:', result); // Log the API response

      if (result.Results && result.Results.length > 0) {
        const vehicleData = result.Results[0];

        // Log the vehicle data for debugging
        console.log('Vehicle Data:', vehicleData);

        // Map NHTSA data to your application's structure
        const mappedVehicleData = {
          make: vehicleData.Make || 'N/A',
          model: vehicleData.Model || 'N/A',
          year: vehicleData.ModelYear || 'N/A',
          vin: searchTerm,
          registrationStatus: 'Active', // Default value
          mileage: 0, // Default value
          specifications: {
            make: vehicleData.Make || 'N/A',
            model: vehicleData.Model || 'N/A',
            year: vehicleData.ModelYear || 'N/A',
            trim: vehicleData.Series || 'N/A',
            madeIn: vehicleData.PlantCountry || 'N/A',
            steeringType: vehicleData.SteeringType || 'N/A',
            fuelType: vehicleData.FuelTypePrimary || 'N/A',
            grossWeight: vehicleData.GrossVehicleWeightRating || 'N/A',
            overallLength: vehicleData.OverallLength || 'N/A',
            standardSeating: vehicleData.SeatingCapacity || 'N/A',
            highwayMileage: vehicleData.FuelEfficiencyHighway || 'N/A',
            invoicePrice: 'N/A', // Not available in NHTSA
            engine: vehicleData.EngineModel || 'N/A',
            style: vehicleData.BodyClass || 'N/A',
            antiBrakeSystem: vehicleData.BrakeSystemType || 'N/A',
            fuelCapacity: vehicleData.FuelCapacity || 'N/A',
            overallHeight: vehicleData.OverallHeight || 'N/A',
            overallWidth: vehicleData.OverallWidth || 'N/A',
            optionalSeating: 'N/A', // Not available in NHTSA
            cityMileage: vehicleData.FuelEfficiencyCity || 'N/A',
            msrp: 'N/A', // Not available in NHTSA
          },
          titleRecords: [], // Default value
          junkSalvageRecords: [], // Default value
          saleRecords: [], // Default value
          problemChecks: {
            floodDamage: 'No problems found!',
            fireDamage: 'No problems found!',
            hailDamage: 'No problems found!',
            saltWaterDamage: 'No problems found!',
            vandalism: 'No problems found!',
            rebuilt: 'No problems found!',
            salvageDamage: 'No problems found!',
          },
        };

        // Navigate to Garage page with the vehicle data
        navigate('/garage', { state: { vehicle: mappedVehicleData } });
      } else {
        setError('No data found for this VIN.');
      }
    } catch (err) {
      console.error('Error fetching NHTSA data:', err); // Log the error
      setError('Failed to fetch data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      {/* Hero Section */}
      <div className="text-center mt-5">
        <h1 className="display-4 fw-bold text-primary">
          Welcome to My Car Website!
        </h1>
        <p className="lead text-muted">
          Explore cars, check their history, and get personalized
          recommendations.
        </p>

        {/* Search Bar */}
        <form
          onSubmit={handleSearch}
          className="mt-4 d-flex justify-content-center"
        >
          <div className="input-group w-100" style={{ maxWidth: '500px' }}>
            {/* Input Field */}
            <input
              type="text"
              placeholder="Enter VIN"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="form-control shadow-sm"
              style={{
                borderTopRightRadius: '0',
                borderBottomRightRadius: '0',
                border: '2px solid #007bff',
                minHeight: '48px',
                lineHeight: '1.5',
                transition:
                  'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
              }}
              onFocus={(e) => {
                e.target.style.transform = 'scale(1.02)';
                e.target.style.boxShadow = '0 4px 8px rgba(0, 123, 255, 0.2)';
              }}
              onBlur={(e) => {
                e.target.style.transform = 'scale(1)';
                e.target.style.boxShadow = 'none';
              }}
            />

            {/* Search Button */}
            <button
              type="submit"
              className="btn btn-primary"
              style={{
                borderTopLeftRadius: '0',
                borderBottomLeftRadius: '0',
                padding: '0.5rem 1.5rem',
                fontSize: '1rem',
                fontWeight: 'bold',
                backgroundColor: '#007bff',
                borderColor: '#007bff',
                minHeight: '48px',
                transition:
                  'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
              }}
              disabled={loading}
              aria-label="Search for vehicle details"
              onMouseEnter={(e) => {
                e.target.style.transform = 'scale(1.05)';
                e.target.style.boxShadow = '0 4px 8px rgba(0, 123, 255, 0.2)';
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'scale(1)';
                e.target.style.boxShadow = 'none';
              }}
            >
              {loading ? (
                <>
                  <span
                    className="spinner-border spinner-border-sm me-2"
                    role="status"
                    aria-hidden="true"
                  ></span>
                  Searching...
                </>
              ) : (
                'Search'
              )}
            </button>
          </div>
        </form>

        {/* Display Error Message */}
        {error && <p className="text-danger mt-3">{error}</p>}
      </div>

      {/* Why Use Our Website Section */}
      <div className="text-center mt-5">
        <h2 className="fw-bold text-secondary">Why Use Our Website?</h2>
        <p className="mb-4 text-muted">
          Discover the key features that make us your go-to platform for vehicle
          information.
        </p>

        {/* Key Features Grid */}
        <div className="row justify-content-center">
          {/* Feature 1: Accident History */}
          <div className="col-md-3 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-car-crash fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">Accident History</h5>
                <p className="card-text text-muted">
                  Check if the vehicle has been involved in any accidents.
                </p>
              </div>
            </div>
          </div>

          {/* Feature 2: Ownership Records */}
          <div className="col-md-3 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-id-card fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">Ownership Records</h5>
                <p className="card-text text-muted">
                  View the complete ownership history of the vehicle.
                </p>
              </div>
            </div>
          </div>

          {/* Feature 3: Market Value Estimation */}
          <div className="col-md-3 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-dollar-sign fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">Market Value Estimation</h5>
                <p className="card-text text-muted">
                  Get an estimated market value for the vehicle.
                </p>
              </div>
            </div>
          </div>

          {/* Feature 4: AI-Powered Recommendations */}
          <div className="col-md-3 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-brain fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">
                  AI-Powered Recommendations
                </h5>
                <p className="card-text text-muted">
                  Get personalized car suggestions and insights based on your
                  preferences.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* AI Section */}
      <div className="text-center mt-5">
        <h2 className="fw-bold text-secondary">Experience the Power of AI</h2>
        <p className="mb-4 text-muted">
          Our AI-driven platform provides personalized recommendations,
          predictive analytics, and "What-If" scenarios to help you make smarter
          decisions about cars.
        </p>

        {/* AI Features List */}
        <div className="row justify-content-center">
          <div className="col-md-4 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-lightbulb fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">Personalized Suggestions</h5>
                <p className="card-text text-muted">
                  Get tailored car recommendations based on your preferences and
                  budget.
                </p>
              </div>
            </div>
          </div>

          <div className="col-md-4 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-chart-line fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">Predictive Analytics</h5>
                <p className="card-text text-muted">
                  Analyze market trends and predict future car values with
                  AI-powered insights.
                </p>
              </div>
            </div>
          </div>

          <div className="col-md-4 col-sm-6 mb-4">
            <div
              className="card shadow-sm h-100"
              style={{ transition: 'transform 0.3s ease' }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = 'scale(1.05)')
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = 'scale(1)')
              }
            >
              <div className="card-body d-flex flex-column align-items-center">
                <i className="fas fa-question-circle fa-3x mb-3 text-primary"></i>
                <h5 className="card-title fw-bold">"What-If" Scenarios</h5>
                <p className="card-text text-muted">
                  Simulate different scenarios to understand the impact of
                  mileage, accidents, and more on car value.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
