import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Button,
  Form,
  Spinner,
  Alert,
  ToggleButton,
  ToggleButtonGroup,
} from 'react-bootstrap';
import { FaSearch } from 'react-icons/fa';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [apiType, setApiType] = useState('nhtsa'); // Default to NHTSA API
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) {
      setError('Please enter a valid VIN.');
      return;
    }
    setError('');
    setLoading(true);

    try {
      let vehicleData = null;

      if (apiType === 'nhtsa') {
        // Fetch data from NHTSA API
        const response = await fetch(
          `https://vpic.nhtsa.dot.gov/api/vehicles/decodevinvalues/${searchTerm}?format=json`
        );
        const result = await response.json();

        if (result.Results && result.Results.length > 0) {
          const nhtsaData = result.Results[0];
          vehicleData = {
            make: nhtsaData.Make || 'N/A',
            model: nhtsaData.Model || 'N/A',
            year: nhtsaData.ModelYear || 'N/A',
            vin: searchTerm,
            registrationStatus: 'Active',
            mileage: 0,
            specifications: {
              make: nhtsaData.Make || 'N/A',
              model: nhtsaData.Model || 'N/A',
              year: nhtsaData.ModelYear || 'N/A',
              trim: nhtsaData.Series || 'N/A',
              madeIn: nhtsaData.PlantCountry || 'N/A',
              steeringType: nhtsaData.SteeringType || 'N/A',
              fuelType: nhtsaData.FuelTypePrimary || 'N/A',
              grossWeight: nhtsaData.GrossVehicleWeightRating || 'N/A',
              overallLength: nhtsaData.OverallLength || 'N/A',
              standardSeating: nhtsaData.SeatingCapacity || 'N/A',
              highwayMileage: nhtsaData.FuelEfficiencyHighway || 'N/A',
              invoicePrice: 'N/A',
              engine: nhtsaData.EngineModel || 'N/A',
              style: nhtsaData.BodyClass || 'N/A',
              antiBrakeSystem: nhtsaData.BrakeSystemType || 'N/A',
              fuelCapacity: nhtsaData.FuelCapacity || 'N/A',
              overallHeight: nhtsaData.OverallHeight || 'N/A',
              overallWidth: nhtsaData.OverallWidth || 'N/A',
              optionalSeating: 'N/A',
              cityMileage: nhtsaData.FuelEfficiencyCity || 'N/A',
              msrp: 'N/A',
            },
            titleRecords: [],
            junkSalvageRecords: [],
            saleRecords: [],
            problemChecks: {
              floodDamage: 'No problems found!',
              fireDamage: 'No problems found!',
              hailDamage: 'No problems found!',
              saltWaterDamage: 'No problems found!',
              vandalism: 'No problems found!',
              rebuilt: 'No problems found!',
              salvageDamage: 'No problems found!',
            },
          };
        } else {
          setError('No data found for this VIN using NHTSA API.');
          setLoading(false);
          return;
        }
      } else if (apiType === 'clearvin') {
        // Fetch data from ClearVIN API
        const clearVinToken =
          'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJlbnZpcm9ubWVudCI6InRlc3QiLCJ1c2VyIjp7ImlkIjoyMzgxMTMsImVtYWlsIjoiZWRpLmJhdG9nY0BnbWFpbC5jb20ifSwidmVuZG9yIjp7ImlkIjozNzUsInN0YXR1cyI6ImFjdGl2ZSJ9LCJpYXQiOjE3NDE2MzgzMjQsImV4cCI6MTc0NDIzMDMyNH0.JV6o9InAZSWTFLxbF7LuusWGhAWQyLPwpp8EaaDScCM';

        const response = await fetch(
          `https://www.clearvin.com/rest/vendor/preview?vin=${searchTerm}`,
          {
            headers: {
              Authorization: `Bearer ${clearVinToken}`,
            },
          }
        );

        const result = await response.json();

        if (result.status === 'ok') {
          const clearVinData = result.result.vinSpec;
          vehicleData = {
            make: clearVinData.make || 'N/A',
            model: clearVinData.model || 'N/A',
            year: clearVinData.year || 'N/A',
            vin: searchTerm,
            registrationStatus: 'Active',
            mileage: 0,
            specifications: {
              make: clearVinData.make || 'N/A',
              model: clearVinData.model || 'N/A',
              year: clearVinData.year || 'N/A',
              trim: clearVinData.trim || 'N/A',
              madeIn: clearVinData.madeIn || 'N/A',
              engine: clearVinData.engine || 'N/A',
              style: clearVinData.style || 'N/A',
              invoicePrice: clearVinData.invoice || 'N/A',
              msrp: clearVinData.msrp || 'N/A',
            },
            titleRecords: result.result.recalls || [],
            junkSalvageRecords: [],
            saleRecords: [],
            problemChecks: {
              floodDamage: 'No problems found!',
              fireDamage: 'No problems found!',
              hailDamage: 'No problems found!',
              saltWaterDamage: 'No problems found!',
              vandalism: 'No problems found!',
              rebuilt: 'No problems found!',
              salvageDamage: 'No problems found!',
            },
          };
        } else {
          setError('No data found for this VIN using ClearVIN API.');
          setLoading(false);
          return;
        }
      }

      // Navigate to Garage page with the vehicle data
      navigate('/garage', { state: { vehicle: vehicleData } });
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to fetch data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      {/* Hero Section */}
      <div className="text-center mt-5">
        <h1 className="fw-bold text-primary">Welcome to AutoInfo</h1>
        <p className="mb-4 text-muted">
          Search for detailed vehicle information using VIN.
        </p>

        {/* API Selection Toggle */}
        <div className="text-center mb-4">
          <ToggleButtonGroup
            type="radio"
            name="api-type"
            value={apiType}
            onChange={setApiType}
          >
            <ToggleButton id="nhtsa-api" value="nhtsa">
              Free NHTSA API
            </ToggleButton>
            <ToggleButton id="clearvin-api" value="clearvin">
              ClearVIN API (Trial)
            </ToggleButton>
          </ToggleButtonGroup>
        </div>

        {/* Search Form */}
        <Form onSubmit={handleSearch}>
          <div className="input-group mb-3 w-50 mx-auto">
            <Form.Control
              type="text"
              placeholder="Enter VIN"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              aria-label="Enter VIN"
              aria-describedby="search-button"
            />
            <Button
              variant="primary"
              id="search-button"
              onClick={handleSearch}
              disabled={loading}
            >
              {loading ? (
                <>
                  <Spinner
                    as="span"
                    animation="border"
                    size="sm"
                    role="status"
                    aria-hidden="true"
                    className="me-2"
                  />
                  Searching...
                </>
              ) : (
                'Search'
              )}
            </Button>
          </div>
        </Form>

        {error && (
          <Alert variant="danger" className="text-center">
            {error}
          </Alert>
        )}

        {/* Why Use Our Website Section */}
        <div className="text-center mt-5">
          <h2 className="fw-bold text-secondary">Why Use Our Website?</h2>
          <p className="mb-4 text-muted">
            Discover the key features that make us your go-to platform for
            vehicle information.
          </p>

          {/* Key Features Grid */}
          <div className="row justify-content-center">
            {/* Feature 1: Accident History */}
            <div className="col-md-3 col-sm-6 mb-4">
              <div
                className="card shadow-sm h-100"
                style={{ transition: 'transform 0.3s ease' }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.transform = 'scale(1.05)')
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.transform = 'scale(1)')
                }
              >
                <div className="card-body d-flex flex-column align-items-center">
                  <i className="fas fa-car-crash fa-3x mb-3 text-primary"></i>
                  <h5 className="card-title fw-bold">Accident History</h5>
                  <p className="card-text text-muted">
                    Check if the vehicle has been involved in any accidents.
                  </p>
                </div>
              </div>
            </div>

            {/* Feature 2: Ownership Records */}
            <div className="col-md-3 col-sm-6 mb-4">
              <div
                className="card shadow-sm h-100"
                style={{ transition: 'transform 0.3s ease' }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.transform = 'scale(1.05)')
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.transform = 'scale(1)')
                }
              >
                <div className="card-body d-flex flex-column align-items-center">
                  <i className="fas fa-file-alt fa-3x mb-3 text-primary"></i>
                  <h5 className="card-title fw-bold">Ownership Records</h5>
                  <p className="card-text text-muted">
                    View the complete ownership history of the vehicle.
                  </p>
                </div>
              </div>
            </div>

            {/* Feature 3: Market Value Estimation */}
            <div className="col-md-3 col-sm-6 mb-4">
              <div
                className="card shadow-sm h-100"
                style={{ transition: 'transform 0.3s ease' }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.transform = 'scale(1.05)')
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.transform = 'scale(1)')
                }
              >
                <div className="card-body d-flex flex-column align-items-center">
                  <i className="fas fa-dollar-sign fa-3x mb-3 text-primary"></i>
                  <h5 className="card-title fw-bold">
                    Market Value Estimation
                  </h5>
                  <p className="card-text text-muted">
                    Get an estimated market value for the vehicle.
                  </p>
                </div>
              </div>
            </div>

            {/* Feature 4: AI-Powered Recommendations */}
            <div className="col-md-3 col-sm-6 mb-4">
              <div
                className="card shadow-sm h-100"
                style={{ transition: 'transform 0.3s ease' }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.transform = 'scale(1.05)')
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.transform = 'scale(1)')
                }
              >
                <div className="card-body d-flex flex-column align-items-center">
                  <i className="fas fa-brain fa-3x mb-3 text-primary"></i>
                  <h5 className="card-title fw-bold">
                    AI-Powered Recommendations
                  </h5>
                  <p className="card-text text-muted">
                    Get personalized recommendations based on vehicle data.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
